;; gorilla-repl.fileformat = 1

;; **
;;; # Experiments with Local Polynomial Kernel Smoother
;;; 
;;; Based on **Analysis of Variance for Functional Data** by *Jin-Ting Zhang*
;; **

;; @@
(ns lpksmoother
  (:require [gorilla-plot.core :as plot]
            [clojure.core.matrix :as m]
            [fda.smoothing.lpk :as lpk]))
(m/set-current-implementation :vectorz)
;; possible implementations :vectorz :clatrix :persistent-vector
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-keyword'>:vectorz</span>","value":":vectorz"}
;; <=

;; **
;;; Let's make some noisy data
;; **

;; @@
(def x (sort (take 100 (repeatedly #(rand)))))
(def y (map #(+ (* 2 (Math/cos (* 2 Math/PI %))) (- (* (rand) 1) 0.5)) x))
(def noisy-data (map vector x y))
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-var'>#&#x27;lpksmoother/noisy-data</span>","value":"#'lpksmoother/noisy-data"}
;; <=

;; **
;;; This is how our noisy data looks like compared to the underlying function:
;; **

;; @@
(def nice-plot (plot/plot #(+ (* 2 (Math/cos (* 2 Math/PI %)))) [0 1]))
(def ugly-plot (plot/list-plot noisy-data :color "#9C6900"))
(plot/compose ugly-plot nice-plot )
